mapper.select:
SELECT	*
FROM	(
		 SELECT	ROWNUM RN, A.*
		 FROM	(
		 		 SELECT 	*
		 		 FROM 		PAYMENT
		 		 WHERE 		B_EMAIL = #{b_email}
		 		 ORDER BY	PAY_DATE DESC
		 ) A
)
WHERE	RN BETWEEN #{start} AND #{end}

SELECT	COUNT(*)
FROM	PAYMENT
WHERE	B_EMAIL = #{b_email}

SELECT	*
FROM	(
		 SELECT ROWNUM RN, A.*
		 FROM	(
		 		 SELECT	*
		 		 FROM		PAYMENT
		 		 WHERE		B_EMAIL = #{b_email} AND PAY_DATE BETWEEN #{firstDate} AND #{secondDate}
		 		 ORDER BY	PAY_DATE DESC
		 ) A
)
WHERE	RN BETWEEN #{start} AND #{end}

SELECT	COUNT(*)
FROM	PAYMENT
WHERE	B_EMAIL = #{b_email} AND PAY_DATE BETWEEN #{firstDate} AND #{secondDate}

SELECT NVL(SUM(PAY_PRICE),0)
FROM PAYMENT
WHERE SUBSTR(TO_CHAR(PAY_DATE,'yyyy-mm-dd'),6,2) =#{num}

SELECT COUNT(*) 
FROM PAYMENT
WHERE SUBSTR(TO_CHAR(PAY_DATE,'yyyy-mm-dd'),6,2) =#{num}

SELECT NVL(SUM(PAY_PRICE),0)
 FROM PAYMENT
 WHERE SUBSTR(TO_DATE(PAY_DATE,'YY-MM-DD'),0,2) = #{num}

SELECT COUNT(*)
   FROM PAYMENT
 WHERE SUBSTR(TO_DATE(PAY_DATE,'YY-MM-DD'),0,2) = #{num}

