mapper.select:

		  SELECT * FROM 
          ( select rownum rn, A.*
            FROM ( 
                   SELECT NO_CODE,NO_ID,NO_TITLE,NO_SUBJECT,NO_DATE,FILECODE,NO_STATUS,
                          NO_IMG,NO_ORIGINIMG 
                   FROM NOTICE WHERE NO_STATUS='1'
                   ORDER BY NO_CODE DESC) A
            WHERE rownum <= #{pageNum} * #{amount} 



			        ) 
    		WHERE rn>(#{pageNum}-1)*#{amount}
