mapper.select:
SELECT * FROM ( SELECT rownum rn, s.* FROM ( select s_email, s_rank,s_nickname,substr(s_tel,1,3)||'-'||substr(s_tel,4,4)||'-'||substr(s_tel,8) as s_tel,s_address, s_zipcode,s_detailaddress,s_author,s_img, s_business,s_status,s_report,s_school,s_major,s_grad, s_carrer,s_me,s_name,s_date,blacklistdate from seller where s_status!='B' order by s_email )s where rownum <= #{pageNum} * #{amount}
)
where rn > (#{pageNum}-1)*#{amount}



  SELECT *
	FROM 
	    (
	    select rownum rn, s.*
	    from v_sellerservicelist s
	    WHERE s_email=#{s_email} AND ROWNUM <=#{pageNum} * #{amount}
	    )

  WHERE rn>(#{pageNum}-1)*#{amount}

SELECT * FROM ( SELECT rownum rn, b.* FROM ( SELECT b_email,b_rank,b_password,b_nickname,substr(b_tel,1,3)||'-'||substr(b_tel,4,4)||'-'||substr(b_tel,8) as b_tel, b_address,b_zipcode,b_detailaddress,b_author,b_img, b_status,b_date,b_report,token, (select c.cat_name from category c join sub_category s on(c.cat_no=s.cat_no) where buyer.field_code=s.sub_no) as cat_name, (select sub_name from sub_category where sub_no=buyer.field_code)as field_code, buyer_coupon,b_name,blacklistdate FROM buyer WHERE b_status !='B' ORDER BY b_email )b
WHERE rownum <= #{pageNum} * #{amount}
)
where rn > (#{pageNum}-1)*#{amount}

mapper.update:
update buyer
set b_rank = #{b_rank}
where b_email=#{b_email}

update seller
set s_rank = #{s_rank}
where s_email=#{s_email}
