mapper.select:
SELECT	*
FROM	(
		 SELECT ROWNUM RN, A.*
		 FROM	(
			SELECT P.*, S.SER_TITLE, S.SER_IMG 
			FROM PROMOTION P, SERVICE S 
			WHERE P.PRO_SERVICE = S.SER_CODE
			AND S.S_EMAIL = #{s_email}
			AND P.PRO_STATUS = 'N'
			ORDER BY P.PRO_START
		) A
	)
WHERE	RN BETWEEN #{start} AND #{end}

mapper.update:
UPDATE PROMOTION SET 
PRO_STATUS = 'Y', PRO_END = sysdate
WHERE PRO_SERVICE = #{pro_service} AND PRO_CODE = #{pro_code}

mapper.select:

SELECT *
FROM PROMOTION
WHERE S_EMAIL = #{s_email}
AND PRO_SERVICE = #{pro_service}
AND PRO_END >= #{pro_end}
AND PRO_STATUS <> 'Y'

mapper.update:
UPDATE PROMOTION SET 
PRO_STATUS = 'Y' , PRO_END = #{pro_end}
WHERE PRO_SERVICE = #{pro_service} AND PRO_CODE = #{pro_code}

UPDATE PROMOTION SET 
PRO_END = #{pro_end}
WHERE PRO_SERVICE = #{pro_service} AND PRO_CODE = #{pro_code}

mapper.select:
SELECT	*
FROM	(
		 SELECT ROWNUM RN, A.*
		 FROM	(
			SELECT P.*, S.SER_TITLE, S.SER_IMG 
			FROM PROMOTION P, SERVICE S 
			WHERE P.PRO_SERVICE = S.SER_CODE
			AND S.S_EMAIL = #{s_email}
			AND P.PRO_STATUS = 'U'
			ORDER BY P.PRO_START
		) A
	)
WHERE	RN BETWEEN #{start} AND #{end}

SELECT	*
FROM	(
		 SELECT ROWNUM RN, A.*
		 FROM	(
			SELECT P.*, S.SER_TITLE, S.SER_IMG 
			FROM PROMOTION P, SERVICE S 
			WHERE P.PRO_SERVICE = S.SER_CODE
			AND S.S_EMAIL = #{s_email}
			AND P.PRO_STATUS = 'Y'
			ORDER BY P.PRO_START
		) A
	)
