mapper.insert:
INSERT INTO REVIEW(REV_CODE, SCODE, REV_NAME, REV_SUB, REV_ID, REV_SER_NAME, REV_IMG, REV_RATE, REV_DATE)
VALUES ((SELECT MAX(NVL(TO_NUMBER(REV_CODE), 0)) + 1 FROM REVIEW)
		, #{scode}
		, #{rev_name}
		, #{rev_sub}
		, #{rev_id}
		, #{rev_ser_name}
		, #{rev_img}
		, #{rev_rate}
		, sysdate
		)

mapper.update:
UPDATE	REVIEW
SET		REV_SUB = #{rev_sub}, REV_RATE = #{rev_rate}, REV_IMG = #{rev_img}
WHERE	REV_CODE = #{rev_code}

mapper.select:
SELECT * FROM ( SELECT ROWNUM RN, A.* FROM (
SELECT R.*, C.*, B.B_IMG, S.S_IMG FROM (SELECT RE.* FROM REVIEW RE, SERVICE SER WHERE RE.SCODE = SER.SER_CODE
AND RE.SCODE = #{ser_code}
AND SER.S_EMAIL=#{s_email}) R LEFT JOIN REVIEW_COMMENT C ON R.REV_CODE = C.REVIEW_CODE LEFT JOIN BUYER B ON R.REV_ID = B.B_EMAIL LEFT JOIN SELLER S ON C.RERE_SEL_ID = S.S_EMAIL ORDER BY TO_NUMBER(REV_CODE) DESC ) A ) WHERE RN BETWEEN #{start} AND #{end}

SELECT		r.* 
FROM 		REVIEW r, SERVICE s 
WHERE 		S_EMAIL= #{s_email} 
			AND SCODE = SER_CODE
			AND SCODE = #{scode}
ORDER BY 	r.rev_date DESC

SELECT m.* FROM SERVICE s ,
(SELECT * FROM REVIEW r , REVIEW_COMMENT c WHERE REV_CODE = REVIEW_CODE(+) ORDER BY REV_DATE DESC) m
WHERE s.s_email = #{s_email} and s.ser_code = scode and s.ser_code = #{ser_code}

SELECT	COUNT(*)
FROM	REVIEW
WHERE	REV_ID = #{rev_id}

SELECT COUNT(*) FROM ( SELECT R.*, C.*, B.B_IMG, S.S_IMG FROM (SELECT RE.* FROM REVIEW RE, SERVICE SER WHERE RE.SCODE = SER.SER_CODE
AND RE.SCODE = #{ser_code}
AND SER.S_EMAIL=#{s_email}) R LEFT JOIN REVIEW_COMMENT C ON R.REV_CODE = C.REVIEW_CODE LEFT JOIN BUYER B ON R.REV_ID = B.B_EMAIL LEFT JOIN SELLER S ON C.RERE_SEL_ID = S.S_EMAIL )

SELECT	COUNT(*)
FROM	REVIEW
WHERE	SCODE = #{scode} AND REV_ID = #{rev_id}
