mapper.update:
UPDATE SERVICE SET
SER_REASON = #{ser_reason}, SER_END = #{ser_end}
WHERE SER_CODE = #{ser_code}

mapper.select:
SELECT * FROM ( SELECT ROWNUM RN, A.* FROM ( SELECT * FROM V_SERVICE_LIST
and ser_title like '%'||#{ser_title}||'%'
and ser_cate like '%'||#{ser_cate}||'%'
ORDER BY REVIEWCOUNT DESC
ORDER BY SER_CODE DESC
) A ) WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
UPDATE SERVICE SET
SER_REASON = #{ser_reason}, SER_END = TO_DATE(TO_CHAR(sysdate,'YYYY-MM-DD')), SER_STATUS = 'Y'
WHERE SER_CODE = #{ser_code}
